function make_slug(e){return e.replace(/[^a-zA-Z0-9 ]/g,"").replace(/ /g,"-")}function toc_init(){jQuery("#toc").append("<p>Contents</p>"),jQuery("h2, h3, h4").each(function(e){var t=jQuery(this),a=make_slug(t.text());t.attr("id",a),jQuery("#toc").append("<a id='link"+e+"' href='#"+a+"' class='"+t.prop("tagName")+"'>"+t.html()+"</a>")})}jQuery(document).ready(toc_init);